package com.pca.gomusic.Adapters;import android.content.Context;import androidx.annotation.NonNull;import androidx.fragment.app.Fragment;import androidx.fragment.app.FragmentManager;import androidx.fragment.app.FragmentPagerAdapter;import com.pca.gomusic.Fragments.AlbumFragment;import com.pca.gomusic.Fragments.ArtistFragment;import com.pca.gomusic.Fragments.SongsListFragment;import com.pca.gomusic.Fragments.StreamingFragment;import com.pca.gomusic.Fragments.VideosListFragment;import com.pca.gomusic.R;public class ViewPagerAdapter extends FragmentPagerAdapter{    private Context context;    public ViewPagerAdapter(@NonNull FragmentManager fm, int behavior, Context context) {        super(fm, behavior);        this.context = context;    }    @NonNull    @Override    public Fragment getItem(int position) {        switch (position) {            case 0:                return new SongsListFragment();            case 1:                return new AlbumFragment();            case 2:                return new ArtistFragment();            case 3:                return new StreamingFragment();            case 4:              return new VideosListFragment();        }        return getItem(0);    }    @Override    public int getCount() {        return 5;    }    @Override    public CharSequence getPageTitle(int position) {        switch (position){            case 0:               return context.getResources().getString(R.string.Songs);            case 1:                return context.getResources().getString(R.string.Album);            case 2:                return context.getResources().getString(R.string.Artist);            case 3:                return context.getResources().getString(R.string.streaming);            case 4:                return context.getResources().getString(R.string.Videos);        }        return null;    }}